int sticks[38]&amp;#123;0&amp;#125;;//å­˜æ”¾æœ¨æ£é•¿åº¦

int side, groupnum, n;
bool DFS(int sum, int number, int pos,int * flag)
&amp;#123;
    if (number == 3)
        return true;

    for(int i=pos;i&amp;lt;n;i++)
    &amp;#123;
        if(sum+sticks[pos]&amp;gt;side)&amp;#123;
            continue;
        &amp;#125;
        if(flag[i]==1)
        &amp;#123;
            continue;
        &amp;#125;
        if(sum+sticks[i]==side)// æ·±åº¦æœç´¢
        &amp;#123;
            if(DFS(0,number+1,0,flag)) return true;
        &amp;#125;
        else
